    } 

    /*
     * Deleting location
     */
    function remove($id)
    {
        $location = $this->Location_model->get_location($id);

        // check if the location exists before trying to delete it
        if(isset($location['id']))
        {
            $this->Location_model->delete_location($id);
            redirect('location/index');
        }
        else
